def test_add(expect, ans):
    try:
        a1=str(ans[0])
        a2=str(ans[1])
    except ValueError:
        return { 'ok': False, 'msg': 'bad input variables' }

    if a2 == "True":
        return { 'ok': 'True', 'msg': a1 }
    if a2 == "Partial":
        return { 'ok': 'Partial', 'msg': a1, 'grade_decimal': '0.6' }
    if a2 == "False":
        return { 'ok': 'False', 'msg': a1 }
